import React from 'react';
import { useMutation, useQuery } from '@apollo/react-hooks'
import { deleteUserMutation, getUsersQuery } from '../queries/queries'
import '../styles/global.css'


function RemoveUser(props) {
  const { loading, error } = useQuery(getUsersQuery);
  const [deleteUser] = useMutation(deleteUserMutation)

  if(loading) return 'Loading...';
  if(error) return `${error.message}`;

  //hvis man har klikket på en af brugerne i list elementerne i komponentet UserList
  //så returneres en div, der beder om bekræftelse inden brugeren slettes fra listen 
  if(props.selected !== "") {
      return (
        <div>
          <p>Are you sure you want to remove user with id {props.selected} from the list?</p>
          <button
            id="remove-user-button"
            onClick={e => {
              deleteUser({ variables: {
                id: props.selected
              },
              refetchQueries: [{ query: getUsersQuery }]
            });
          }}>Remove</button>
          </div>
        )
      } else if(props.selected === "") {
        return (
          <div>
            <p>Click user to delete</p>
          </div>
    )
  }
  };

export default RemoveUser;
